import React, { useState, useEffect } from 'react';
import { View, Button, TextInput, StyleSheet } from 'react-native';
import axios from 'axios';
import { useNavigation } from '@react-navigation/native';
import { RelatorioAluno } from './RelatorioAluno';

export function CadastraEntrada() {
  const navigation = useNavigation();
  const [registroEntrada, setRegistroEntrada] = useState([]);
  const [novoRegistroEntrada, setNovoRegistroEntrada] = useState({
    data_hora: '',
    cpf: '',
  });
  const [exibirLista, setExibirLista] = useState(false);

  useEffect(() => {
    fetchRegistroEntrada();
  }, []);

  const fetchRegistroEntrada = async () => {
    try {
      const response = await axios.get('http://localhost:8090/registroentrada');
      setRegistroEntrada(response.data);
    } catch (error) {
      console.error('Erro ao buscar registroentrada:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:8090/registroentrada', novoRegistroEntrada);
      fetchRegistroEntrada();
      setNovoRegistroEntrada({
        data_hora: '',
        cpf: '',
      });
    } catch (error) {
      console.error('Erro ao criar registroentrada:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8090/registroentrada/${id}`);
      fetchRegistroEntrada();
    } catch (error) {
      console.error('Erro ao excluir registroentrada:', error);
    }
  };

  const handleToggleLista = () => {
    setExibirLista(!exibirLista);
  };

  return (
    <View style={styles.container}>
      <Button title={exibirLista ? "Esconder Registros" : "Ver Registros"} onPress={handleToggleLista} />

      {exibirLista && (
        <RelatorioAluno registros={registroEntrada} />
      )}

      <TextInput
        style={styles.input}
        placeholder="Data e Hora"
        value={novoRegistroEntrada.data_hora}
        onChangeText={(text) => setNovoRegistroEntrada({ ...novoRegistroEntrada, data_hora: text })}
      />
      <TextInput
        style={styles.input}
        placeholder="CPF"
        value={novoRegistroEntrada.cpf}
        onChangeText={(text) => setNovoRegistroEntrada({ ...novoRegistroEntrada, cpf: text })}
      />
      <Button title="Adicionar Entrada" onPress={handleSubmit} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    height: 40,
    width: 300,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    padding: 10,
  },
});
