import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

export function RelatorioAluno({ registros }) {
  return (
    <View style={styles.container}>
      <View style={styles.headerContainer}>
        <Text style={styles.headerText}>Relat√≥rios</Text>
      </View>

      {registros.map((registro, index) => (
        <View key={index} style={styles.rectangle}>
          <Text style={styles.rectangleText}>{registro.data_hora} - {registro.cpf}</Text>
        </View>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'center',
    backgroundColor: 'white',
  },
  headerContainer: {
    marginBottom: 20,
  },
  headerText: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  rectangle: {
    width: 354,
    height: 71,
    backgroundColor: '#EFEFEF',
    marginVertical: 10,
  },
  rectangleText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'black',
    position: 'absolute',
    top: -20,
    left: 10,
  },
});
