import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigation } from '@react-navigation/native';


export function RegistraAluno() {
  const navigation = useNavigation();
  const [alunos, setAlunos] = useState([]);
  const [novoAluno, setNovoAluno] = useState({
    rg: '',
    cpf: '',
    numCelular: '',
    email: '',
    dataNasc: '',
    nome: '',
    idmatricula: '',
  });


  useEffect(() => {
    fetchAlunos();
  }, []);


  const fetchAlunos = async () => {
    try {
      const response = await axios.get('http://localhost:8090/aluno');
      setAlunos(response.data);
    } catch (error) {
      console.error('Erro ao buscar alunos:', error);
    }
  };


  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('http://localhost:8090/aluno', novoAluno);
      fetchAlunos();
      setNovoAluno({
        rg: '',
        cpf: '',
        numCelular: '',
        email: '',
        dataNasc: '',
        nome: '',
        idmatricula: '',
      });
    } catch (error) {
      console.error('Erro ao criar aluno:', error);
    }
  };


  const handleDelete = async (rg) => {
    try {
      await axios.delete(`http://localhost:8090/aluno/${rg}`);
      fetchAlunos();
    } catch (error) {
      console.error('Erro ao excluir aluno:', error);
    }
  };


  return (
    <div style={styles.container}>
      {/* Cabeçalho */}
      <h1 style={styles.header}>Gerenciamento de Alunos</h1>

      {/* Formulário de adição de aluno */}
      <form style={styles.form} onSubmit={handleSubmit}>
        {/* Inputs do formulário */}
        {/* ... (seu código existente) */}
        <button style={styles.addButton} type="submit">Adicionar Aluno</button>
      </form>

      {/* Lista de alunos */}
      <ul style={styles.ul}>
        {alunos.map((aluno) => (
          <li key={aluno.rg} style={styles.li}>
            {aluno.rg} - {aluno.nome} ({aluno.dataNasc})
            <button style={styles.deleteButton} onClick={() => handleDelete(aluno.rg)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

// Estilos
const styles = {
  container: {
    maxWidth: '600px',
    margin: 'auto',
    padding: '20px',
    backgroundColor: '#f8f8f8',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  header: {
    textAlign: 'center',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '20px',
  },
  addButton: {
    backgroundColor: '#4caf50',
    color: 'white',
    border: 'none',
    padding: '10px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  ul: {
    listStyle: 'none',
    padding: '0',
  },
  li: {
    marginBottom: '10px',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '4px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  deleteButton: {
    backgroundColor: '#f44336',
    color: 'white',
    border: 'none',
    padding: '8px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};
