    };

    return (
        <View style={styles.container}>
            <View style={styles.headerContainer}>
                <Text style={styles.headerText}> Notificacoes</Text>
            </View>
            
            {/* Adicione os blocos retangulares com texto acima */}
            <View style={styles.rectangle}>
            </View>
            <View style={styles.rectangle}>
            </View>
            <View style={styles.rectangle}>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'flex-start', // Leva os elementos ao topo
        alignItems: 'center',
        backgroundColor: 'white',
    },
    headerContainer: {
        marginBottom: 20,
    },
    headerText: {
        fontSize: 24,
        fontWeight: 'bold',
    },
    rectangle: {
        width: 354,
        height: 71,
        backgroundColor: '#EFEFEF',
        marginVertical: 10,
    },
    rectangleText: {
        fontSize: 16,
        fontWeight: 'bold',
        color: 'black',
        position: 'absolute', // Posiciona o texto absolutamente dentro do retângulo
        top: -20, // Posiciona o texto acima do retângulo
        left: 10, // Espaçamento à esquerda do retângulo
    },
});
